#pragma config(Hubs,  S1, HTMotor,  HTMotor,  none,     none)
#pragma config(Motor,  mtr_S1_C1_1,     Right,         tmotorTetrix, PIDControl, reversed, encoder)
#pragma config(Motor,  mtr_S1_C1_2,     Left,          tmotorTetrix, PIDControl, encoder)
#pragma config(Motor,  mtr_S1_C2_1,     motorF,        tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C2_2,     ForkLift,      tmotorTetrix, PIDControl, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "JoystickDriver.c"
int MotorPower = 75;

task main()
{
	waitForStart();

	nMotorEncoder[ForkLift] = 0;
	nMotorEncoder[Right] = 0;
	nMotorEncoder[Left] = 0;

	while(nMotorEncoder[Left] < 840)
	{
		motor[Left] = MotorPower;
		motor[Right] = 80;
	}

	motor[Left] = 0;
	motor[Right] = 0;
	wait1Msec(10);

	while(nMotorEncoder[ForkLift] < 2201)
	{
		motor[ForkLift] = MotorPower;
	}

	motor[ForkLift] = 0;
	wait1Msec(10);

	ClearTimer(T1);

	while(time1[T1] < 2300)
	{
		motor[Left] = 30;
		motor[Right] = 35;
	}

	motor[Left] = 0;
	motor[Right] = 0;
	wait1Msec(10);

	while(nMotorEncoder[ForkLift] > 0)
	{
		motor[ForkLift] = -MotorPower;
	}

	motor[ForkLift] = 0;
	wait1Msec(10);

	nMotorEncoder[Left] = 0;

	while(nMotorEncoder[Left] > -190)
	{
		motor[Left] = -MotorPower;
		motor[Right] = -MotorPower;
	}

	motor[Left] = 0;
	motor[Right] = 0;
	wait1Msec(10);
}
