#pragma config(Hubs,  S1, HTMotor,  none,     none,     none)
#pragma config(Sensor, S2,     gyroSensor,     sensorI2CHiTechnicGyro)
#pragma config(Motor,  mtr_S1_C1_1,     mot1,          tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C1_2,     mot2,          tmotorTetrix, openLoop, reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#pragma debuggerWindows("nxtLCDScreen");
//#pragma debuggerWindows("Servos");
#pragma debuggerWindows("joystickGame");

#define TANK_2M

#include "JoystickDriver.c"
#include "../library/controllers/controller_defines.h"
#include "../library/sensors/gyro.c"
#include "../library/controllers/tank_controller.c"
#include "../library/drive/gyro_drive.c"

int TURN_TEST = 45; // will turn left 45 degrees for test

bool failed = false;
task main()
{
	//StartTask(Drive);
	ASSUME_CONTROLLER_INPUT = false;
	RegisterDriveMotors(mot1, mot2);
	RegisterGyroSensor(gyroSensor);
	startGyro();
	resetGyro();
	TurnLeft(75);
	DisplayGyroAngleOnNXTDisplay();
	while(true)
	{
		DisplayGyroAngleOnNXTDisplay();

		// Stop if the gyro reading is invalid
		if (!gyroValid()) {
			failed = true;
			break;
		}
		// Turn until we exceed the requested (absolute) angle
		int angle = abs(readGyro());
		if (angle > TURN_TEST) {
			break;
		}
	}
	StopAllDriveMotors();
	if(failed == true)
		nxtDisplayTextLine(1, "FAILED");
	else
		nxtDisplayTextLine(1, "SUCCESS");

	while(true)
	{
	}

}
