#pragma config(Hubs,  S1, HTMotor,  none,     none,     none)
#pragma config(Sensor, S2,     gyroSensor,     sensorI2CHiTechnicGyro)
#pragma config(Motor,  motorA,           ,             tmotorNXT, openLoop, encoder)
#pragma config(Motor,  motorB,           ,             tmotorNXT, openLoop, encoder)
#pragma config(Motor,  mtr_S1_C1_1,     mot1,          tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C1_2,     mot2,          tmotorTetrix, openLoop, reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

//#pragma debuggerWindows("nxtLCDScreen");
//#pragma debuggerWindows("Servos");
#pragma debuggerWindows("joystickGame");

#define TANK_2M

#include "JoystickDriver.c"
#include "../library/drive_modes/drive_modes.h"
#include "../library/controllers/controller_defines.h"
#include "../library/sensors/gyro.c"

task main()
{
	RegisterDriveMotors(mot1, mot2);
	RegisterGyroSensor(gyroSensor);
	startGyro();

	while(true)
	{
		getJoystickSettings(joystick);

		if(abs(joystick.joy1_y1)>ANALOG_DEAD_ZONE)
			DriveLeftMotors(joystick.joy1_y1);
		else
			StopLeftDriveMotors();

		if(abs(joystick.joy1_y2)>ANALOG_DEAD_ZONE)
			DriveRightMotors(joystick.joy1_y2);
		else
			StopRightDriveMotors();

		if(joy1Btn(2) == 1)
		{
			resetGyro();
			TurnLeft(50);
			while(readGyro()<90)
			{}
			StopAllDriveMotors();

		}
	}

}
